import React, { useState, useEffect } from 'react';
import CartItem from './CartItem';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Cart({ cart, onRemove, onQuantityChange }) {
  const [customerInfo, setCustomerInfo] = useState({
    name: '',
    phone: '',
    dni: '',
    email: ''
  });
  const [paymentMethod, setPaymentMethod] = useState('');
  const [mp, setMp] = useState(null);

  useEffect(() => {
    // Inicializar MercadoPago
    const script = document.createElement('script');
    script.src = "https://sdk.mercadopago.com/js/v2";
    script.onload = () => {
      const mp = new window.MercadoPago('TEST-39b72eae-048a-4888-9401-6910724b5b7c', {
        locale: 'es-AR'
      });
      setMp(mp);
    };
    document.body.appendChild(script);
  }, []);

  const total = cart.reduce((acc, item) => acc + item.price * item.quantity, 0);

  const handleInputChange = (e) => {
    setCustomerInfo({ ...customerInfo, [e.target.name]: e.target.value });
  };

  const handlePaymentMethodChange = (e) => {
    setPaymentMethod(e.target.value);
  };

  const handlePayment = async (e) => {
    e.preventDefault();
    
    if (paymentMethod === 'mercadopago') {
      if (!mp) {
        console.error('MercadoPago no está inicializado');
        return;
      }

      const orderData = {
        quantity: 1,
        description: 'Compra en NiniShop',
        price: total
      };

      try {
        const response = await fetch('https://api.mercadopago.com/checkout/preferences', {
          method: 'POST',
          headers: {
            Authorization: 'Bearer TEST-7806538510261573-072923-e25f3169c0b53591b2af7d4f13bb81ec-613424466',
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            items: [orderData],
            payer: {
              name: customerInfo.name,
              email: customerInfo.email,
              identification: {
                type: 'DNI',
                number: customerInfo.dni
              }
            }
          })
        });

        const preference = await response.json();

        mp.checkout({
          preference: {
            id: preference.id
          },
          render: {
            container: '.cho-container',
            label: 'Pagar',
          }
        });
      } catch (error) {
        console.error('Error al crear la preferencia de pago:', error);
      }
    } else if (paymentMethod === 'transfer') {
      // Mostrar datos bancarios
      alert('Datos para transferencia: \nAlias: TU_ALIAS\nCBU: TU_CBU\nNombre: TU_NOMBRE');
      // Aquí podrías guardar los datos de la orden en localStorage
      saveOrderToLocalStorage();
    }
  };

  const saveOrderToLocalStorage = () => {
    const order = {
      id: `ORDEN_${Date.now()}`,
      customer: customerInfo,
      items: cart,
      total: total,
      status: 'pending',
      createdAt: new Date().toISOString()
    };

    const orders = JSON.parse(localStorage.getItem('orders') || '[]');
    orders.push(order);
    localStorage.setItem('orders', JSON.stringify(orders));
  };

  const handleRemove = (item) => {
    onRemove(item);
  };

  const handleQuantityChange = (item, newQuantity) => {
    onQuantityChange(item, newQuantity);
  };

  if (cart.length === 0) {
    return (
      <div className="cart-container">
        <h2>Carrito de Compras</h2>
        <p>Tu carrito está vacío</p>
      </div>
    );
  }

  return (
    <div className="cart-container">
      <h2>Carrito de Compras</h2>
      <ul className="cart-items">
        {cart.map((item) => (
          <CartItem
            key={item.id}
            item={item}
            onRemove={handleRemove}
            onQuantityChange={handleQuantityChange}
          />
        ))}
      </ul>
      <div className="cart-summary">
        <h3>Resumen de Compra</h3>
        <p className="total">Total: ${total.toFixed(2)}</p>
        <form onSubmit={handlePayment}>
          <input
            type="text"
            name="name"
            value={customerInfo.name}
            onChange={handleInputChange}
            placeholder="Nombre completo"
            required
          />
          <input
            type="tel"
            name="phone"
            value={customerInfo.phone}
            onChange={handleInputChange}
            placeholder="Número de teléfono"
            required
          />
          <input
            type="text"
            name="dni"
            value={customerInfo.dni}
            onChange={handleInputChange}
            placeholder="DNI"
            required
          />
          <input
            type="email"
            name="email"
            value={customerInfo.email}
            onChange={handleInputChange}
            placeholder="Correo electrónico"
            required
          />
          <p>Punto de retiro: Colon 1358</p>
          
          <div>
            <input
              type="radio"
              id="mercadopago"
              name="paymentMethod"
              value="mercadopago"
              checked={paymentMethod === 'mercadopago'}
              onChange={handlePaymentMethodChange}
              required
            />
            <label htmlFor="mercadopago">MercadoPago</label>
          </div>
          <div>
            <input
              type="radio"
              id="transfer"
              name="paymentMethod"
              value="transfer"
              checked={paymentMethod === 'transfer'}
              onChange={handlePaymentMethodChange}
            />
            <label htmlFor="transfer">Transferencia bancaria</label>
          </div>
          
          <button type="submit">Pagar ${total.toFixed(2)}</button>
        </form>
        <div className="cho-container"></div>
      </div>
    </div>
  );
}

export default Cart;

