import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function AdminCrud() {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    id: null,
    name: '',
    price: '',
    image: ''
  });
  const [alertMessage, setAlertMessage] = useState('');
  const [editing, setEditing] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get('http://localhost:3001/products');
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const handleInputChange = (e) => {
    setNewProduct({
      ...newProduct,
      [e.target.name]: e.target.value
    });
  };

  const handleFinishChanges = () => {
    navigate("/");
  };

  const handleAddProduct = async () => {
    try {
      if (editing) {
        await axios.put(`http://localhost:3001/products/${newProduct.id}`, newProduct);
        setAlertMessage('Producto actualizado con éxito');
      } else {
        await axios.post('http://localhost:3001/products', newProduct);
        setAlertMessage('Producto añadido con éxito');
      }
      setNewProduct({ id: null, name: '', price: '', image: '' });
      setEditing(false);
      fetchProducts();
    } catch (error) {
      console.error('Error adding/updating product:', error);
    }
  };

  const handleDeleteProduct = async (id) => {
    try {
      await axios.delete(`http://localhost:3001/products/${id}`);
      setAlertMessage('Producto eliminado');
      fetchProducts();
    } catch (error) {
      console.error('Error deleting product:', error);
    }
  };

  const handleEditProduct = (product) => {
    setEditing(true);
    setNewProduct(product);
  };

  return (
    <div>
      <h1>Administración de Productos</h1>
      <div>
        <h2>{editing ? 'Editar Producto' : 'Añadir Producto'}</h2>
        <input
          type="text"
          name="name"
          placeholder="Nombre"
          value={newProduct.name}
          onChange={handleInputChange}
        />
        <input
          type="number"
          name="price"
          placeholder="Precio"
          value={newProduct.price}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="image"
          placeholder="URL de la imagen"
          value={newProduct.image}
          onChange={handleInputChange}
        />
        <button onClick={handleAddProduct}>{editing ? 'Guardar' : 'Añadir'}</button>
      </div>
      {alertMessage && <p>{alertMessage}</p>}
      <ul>
        {products.map((product) => (
          <li key={product.id}>
            <img src={product.image} alt={product.name} />
            <p>Nombre: {product.name}</p>
            <p>Precio: {product.price}</p>
            <button onClick={() => handleEditProduct(product)}>Editar</button>
            <button onClick={() => handleDeleteProduct(product.id)}>Eliminar</button>
          </li>
        ))}
      </ul>
      <button className="btn btn-finish" onClick={handleFinishChanges}>
        Guardar cambios y cerrar sesión
      </button>
    </div>
  );
}

export default AdminCrud;
