import React, { useState } from 'react';
import axios from 'axios';

function Register() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      console.log ('')
      return;
    }

    try {
      const response = await axios.post('http://localhost:3001/users', {
        username,
        password,
      });
      console.log('Usuario registrado:', response.data);
      // Aquí puedes redirigir al usuario o hacer cualquier otra acción después del registro exitoso
    } catch (error) {
      console.error('Error al registrar usuario:', error);
    }
  };

  const handleUsernameChange = (e) => {
    setUsername(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleConfirmPasswordChange = (e) => {
    setConfirmPassword(e.target.value);
  };

  return (
    <div className="overlay">
      <div className="register-container">
        <h2 className="titulo-registro">Se parte de Barbieland</h2>
        <h5 className="bienvenida"> Crea un usuario y contraseña</h5>
        <form onSubmit={handleSubmit} className="register-form">
          <div className="form-group">
            <input
              className="form-input"
              id="username" // Agrega un id
              type="text"
              placeholder="Usuario"
              value={username}
              onChange={handleUsernameChange}
            />
          </div>
          <div className="form-group">
            <input
              className="form-input"
              id="password" // Agrega un id
              type={showPassword ? 'text' : 'password'}
              placeholder="Contraseña"
              value={password}
              onChange={handlePasswordChange}
            />
          </div>
          <div className="form-group">
            <input
              className="form-input"
              id="confirmPassword" // Agrega un id
              type={showPassword ? 'text' : 'password'}
              placeholder="Confirmar contraseña"
              value={confirmPassword}
              onChange={handleConfirmPasswordChange}
            />
          </div>
          <button type="submit" className="register-btn">
            Registrarse
          </button>
        </form>
      </div>
    </div>
  );
}

export default Register;

