import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PanelAdmin = () => {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({ name: '', price: '' });
  const [editingProduct, setEditingProduct] = useState(null);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get('http://localhost:3000/products');
      setProducts(response.data);
    } catch (error) {
      console.error('Error al obtener los productos:', error);
    }
  };

  const addProduct = async () => {
    try {
      const response = await axios.post('http://localhost:3000/products', newProduct);
      setProducts([...products, response.data]);
      setNewProduct({ name: '', price: '' });
    } catch (error) {
      console.error('Error al agregar el producto:', error);
    }
  };

  const deleteProduct = async (id) => {
    try {
      await axios.delete(`http://localhost:3000/products/${id}`);
      setProducts(products.filter((product) => product.id !== id));
    } catch (error) {
      console.error('Error al eliminar el producto:', error);
    }
  };

  const editProduct = (product) => {
    setEditingProduct(product);
    setNewProduct(product);
  };

  const updateProduct = async () => {
    try {
      const response = await axios.put(`http://localhost:3000/products/${editingProduct.id}`, newProduct);
      setProducts(products.map((product) => (product.id === response.data.id ? response.data : product)));
      setEditingProduct(null);
      setNewProduct({ name: '', price: '' });
    } catch (error) {
      console.error('Error al actualizar el producto:', error);
    }
  };

  return (
    <div>
      <h2>Panel de Administraci√≥n</h2>
      {/* Formulario para agregar/editar productos */}
      <form onSubmit={(e) => {
        e.preventDefault();
        editingProduct ? updateProduct() : addProduct();
      }}>
        <input
          type="text"
          placeholder="Nombre"
          value={newProduct.name}
          onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
        />
        <input
          type="text"
          placeholder="Precio"
          value={newProduct.price}
          onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
        />
        <button type="submit">{editingProduct ? 'Guardar' : 'Agregar'}</button>
      </form>

      {/* Lista de productos */}
      <ul>
        {products.map((product) => (
          <li key={product.id}>
            {product.name} - ${product.price}
            <button onClick={() => editProduct(product)}>Editar</button>
            <button onClick={() => deleteProduct(product.id)}>Eliminar</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default PanelAdmin;
