import React, { useState, useEffect } from 'react';
import CartItem from './CartItem';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Cart({ cart, onRemove, onQuantityChange }) {
  const [showPaymentForm, setShowPaymentForm] = useState(false);
  const [customerInfo, setCustomerInfo] = useState({
    name: '',
    phone: '',
    dni: '',
    email: '',
    additionalNotes: ''
  });
  const navigate = useNavigate();

  const total = cart.reduce((acc, item) => acc + item.price * item.quantity, 0);

  const handleInputChange = (e) => {
    setCustomerInfo({ ...customerInfo, [e.target.name]: e.target.value });
  };

  const handleShowPaymentForm = () => {
    setShowPaymentForm(true);
  };

  const saveOrderToDb = async () => {
    const order = {
      id: `ORDEN_${Date.now()}`,
      customer: customerInfo,
      items: cart.map(item => ({
        name: item.name,
        quantity: item.quantity,
        price: item.price
      })),
      total: total,
      status: 'pending',
      createdAt: new Date().toISOString()
    };

    try {
      const response = await axios.post('http://localhost:3000/orders', order);
      console.log('Orden guardada en db.json:', response.data);
      return response.data;
    } catch (error) {
      console.error('Error al guardar la orden:', error);
      throw error;
    }
  };

  const handlePayment = async (e) => {
    e.preventDefault();
    
    try {
      const savedOrder = await saveOrderToDb();
      // Aquí simularemos un pago exitoso
      console.log('Pago simulado exitoso para la orden:', savedOrder.id);
      
      // Limpia el carrito (asumiendo que tienes una función para esto)
      // clearCart();
      
      // Redirige a una página de éxito
      navigate('/success', { state: { orderId: savedOrder.id } });
    } catch (error) {
      console.error('Error en el proceso de pago:', error);
      // Maneja el error (muestra un mensaje al usuario, etc.)
    }
  };
  
  if (cart.length === 0) {
    return (
      <div className="cart-container">
        <h2>Carrito de Compras</h2>
        <p>Tu carrito está vacío</p>
      </div>
    );
  }

  return (
    <div className="cart-container">
      <h2>Carrito de Compras</h2>
      <ul className="cart-items">
        {cart.map((item) => (
          <CartItem
            key={item.id}
            item={item}
            onRemove={onRemove}
            onQuantityChange={onQuantityChange}
          />
        ))}
      </ul>
      <div className="cart-summary">
        <h3>Resumen de Compra</h3>
        {cart.map((item) => (
          <p key={item.id}>
            {item.quantity} x {item.name} - ${(item.price * item.quantity).toFixed(2)}
          </p>
        ))}
        <p className="total">Total: ${total.toFixed(2)}</p>
        {!showPaymentForm ? (
          <button onClick={handleShowPaymentForm}>Pagar</button>
        ) : (
          <form onSubmit={handlePayment}>
            <input
              type="text"
              name="name"
              value={customerInfo.name}
              onChange={handleInputChange}
              placeholder="Nombre completo"
              required
            />
            <input
              type="tel"
              name="phone"
              value={customerInfo.phone}
              onChange={handleInputChange}
              placeholder="Número de teléfono"
              required
            />
            <input
              type="text"
              name="dni"
              value={customerInfo.dni}
              onChange={handleInputChange}
              placeholder="DNI"
              required
            />
            <input
              type="email"
              name="email"
              value={customerInfo.email}
              onChange={handleInputChange}
              placeholder="Correo electrónico"
              required
            />
            <textarea
              name="additionalNotes"
              value={customerInfo.additionalNotes}
              onChange={handleInputChange}
              placeholder="Notas adicionales"
            />
            <p>Punto de retiro: Colon 1358</p>
            
            <button type="submit">Pagar</button>
          </form>
        )}
      </div>
    </div>
  );
}

export default Cart;
