import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleUsernameChange = (e) => {
    setUsername(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch("http://localhost:3000/usuarios")
      .then((response) => response.json())
      .then((users) => {
        const user = users.find(
          (u) => u.username === username && u.password === password
        );
        if (username === "admin" && password === "123") {
          alert("eres administrador");
          navigate("/dashboard");
        } else if (user) {
          alert("bienvenido/a");
          navigate("/");
        } else {
          alert("usuario o contrase침a incorrecto ");
        }
      })
      .catch((error) => alert("Error al iniciar sesi칩n:", error));
  };

  return (
    <div className="overlay">
      <form onSubmit={handleSubmit} className="login-form">
        <div className="con">
          <header className="head-form">
            <h2>Iniciar Sesi칩n</h2>
            <p>Coloca tu usuario y contrase침a</p>
          </header>
          <br />
          <div className="field-set">
            <span className="input-item">
              <i className="fa fa-user-circle"></i>
            </span>
            <input
              className="form-input"
              id="username" // Agrega un id
              type="text"
              placeholder="@usuario"
              value={username}
              onChange={handleUsernameChange}
              required
            />
            <br />
            <span className="input-item">
              <i className="fa fa-key"></i>
            </span>
            <input
              className="form-input"
              id="password" // Agrega un id
              type={showPassword ? 'text' : 'password'}
              placeholder="Contrase침a"
              value={password}
              onChange={handlePasswordChange}
              required
            />
            <span className="emojis" onClick={togglePasswordVisibility}>
              {showPassword ? '游뗻 Ocultar' : '游 Mostrar'}
            </span>
            <br />
            <button type="submit" className="log-in">
              Ingresar
            </button>
          </div>
          <div className="other">
            <span className="new-user">쯅uevo? Reg칤strate aqu칤!</span>
            <Link to="/register" className="btn submits sign-up">
              Registrarse
              <i className="fa fa-user-plus" aria-hidden="true"></i>
            </Link>
          </div>
        </div>
      </form>
    </div>
  );
};

export default Login;
