// SuccessPage.js
import React, { useEffect } from 'react';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';
import '../components/succespage.css';

function SuccessPage() {
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    const searchParams = new URLSearchParams(location.search);
    const paymentId = searchParams.get('payment_id');
    const status = searchParams.get('status');

    if (status === 'approved') {
      const cart = JSON.parse(localStorage.getItem('cart') || '[]');
      const customerInfo = JSON.parse(localStorage.getItem('customerInfo') || '{}');
      const total = cart.reduce((acc, item) => acc + item.price * item.quantity, 0);

      const orderData = {
        paymentId,
        customerInfo,
        cart,
        total,
        status: 'completed',
        createdAt: new Date().toISOString()
      };

      axios.post('http://localhost:3001/orders', orderData)
        .then(response => {
          console.log('Orden guardada exitosamente');
          localStorage.removeItem('cart');
          localStorage.removeItem('customerInfo');
        })
        .catch(error => {
          console.error('Error al guardar la orden:', error);
        });
    }
  }, [location]);

  const handleContinueShopping = () => {
    navigate('/');
  };

  return (
    <div className="success-container">
      <div className="success-content">
        <h1 className="success-title">Â¡Pago Exitoso!</h1>
        <div className="success-icon">ðŸ’—</div>
        <p className="success-message">Gracias por tu compra. Nos contactaremos a la brevedad.
          Soporte al cliente: 3816591850
        </p>
        <button className="continue-shopping-btn" onClick={handleContinueShopping}>
          Seguir Comprando
        </button>
      </div>
    </div>
  );
}

export default SuccessPage;
