import React, { useEffect } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';

function SuccessPage() {
  const location = useLocation();

  useEffect(() => {
    const searchParams = new URLSearchParams(location.search);
    const paymentId = searchParams.get('payment_id');
    const status = searchParams.get('status');

    if (status === 'approved') {
      // Recuperar información del carrito y del cliente
      const cart = JSON.parse(localStorage.getItem('cart') || '[]');
      const customerInfo = JSON.parse(localStorage.getItem('customerInfo') || '{}');
      const total = cart.reduce((acc, item) => acc + item.price * item.quantity, 0);

      // Enviar información de la orden al servidor
      axios.post('http://localhost:3000/orders', {
        paymentId,
        customerInfo,
        cart,
        total,
        additionalNotes: customerInfo.additionalNotes || '',
      })
      .then(response => {
        console.log('Compra realizada exitosamente');
        // Limpiar el carrito y la información del cliente después de un pago exitoso
        localStorage.removeItem('cart');
        localStorage.removeItem('customerInfo');
      })
      .catch(error => {
        console.error('Error al enviar la información del pedido:', error);
      });
    }
  }, [location]);
  

  return (
    <div>
      <h1>¡Pago Exitoso!</h1>
      <p>Gracias por tu compra. Recibirás un correo electrónico con los detalles de tu pedido.</p>
    </div>
  );
}

export default SuccessPage;
