import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const onLogin = async (username, password) => {
  try {
    const response = await axios.get('http://localhost:3000/users', {
      params: {
        username,
        password,
      },
    });

    if (response.data.length > 0) {
      // Usuario autenticado correctamente
      console.log('Inicio de sesi贸n exitoso');
      // Aqu铆 puedes redirigir al usuario o hacer cualquier otra acci贸n despu茅s del inicio de sesi贸n exitoso
    } else {
      // Credenciales incorrectas
      console.log('Credenciales incorrectas');
    }
  } catch (error) {
    console.error('Error al iniciar sesi贸n:', error);
  }
};

const Login = ({ onLogin }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  const handleUsernameChange = (e) => {
    setUsername(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
  
    if (password !== confirmPassword) {
      // Manejar el caso cuando las contrase帽as no coinciden
      return;
    }
  
    try {
      const response = await axios.post('http://localhost:3000/users', {
        username,
        password,
      });
      console.log('Usuario registrado:', response.data);
      // Aqu铆 puedes redirigir al usuario o hacer cualquier otra acci贸n despu茅s del registro exitoso
    } catch (error) {
      console.error('Error al registrar usuario:', error);
    }
  };

  return (
    <div className="overlay">
      <form onSubmit={handleSubmit} className="login-form">
        <div className="con">
          <header className="head-form">
            <h2>Iniciar Sesi贸n</h2>
            <p>Coloca tu usuario y contrase帽a</p>
          </header>
          <br />
          <div className="field-set">
            <span className="input-item">
              <i className="fa fa-user-circle"></i>
            </span>
            <input
              className="form-input"
              id="txt-input"
              type="text"
              placeholder="@usuario"
              value={username}
              onChange={handleUsernameChange}
              required
            />
            <br />
            <span className="input-item">
              <i className="fa fa-key"></i>
            </span>
            <input
              className="form-input"
              type={showPassword ? 'text' : 'password'}
              placeholder="Contrase帽a"
              id="pwd"
              name="password"
              value={password}
              onChange={handlePasswordChange}
              required
            />
            <span className="emojis" onClick={togglePasswordVisibility}>
            {showPassword ? ' Ocultar' : ' Mostrar'}
          </span>
            <br />
            <button type="submit" className="log-in">
              Ingresar
            </button>
          </div>
          <div className="other">
            <span className="new-user">驴Nuevo? Reg铆strate aqu铆!</span>
            <Link to="/register" className="btn submits sign-up">
              Registrarse
              <i className="fa fa-user-plus" aria-hidden="true"></i>
            </Link>
          </div>
        </div>
      </form>
    </div>
  );
};

export default Login;
