import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './admin.css';
import { FaEdit, FaTrash } from 'react-icons/fa';

const PanelAdmin = () => {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({ name: '', price: '', image: null });
  const [editingProduct, setEditingProduct] = useState(null);

  useEffect(() => {
    console.log ('hols')
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get('http://localhost:3001/products');
      setProducts(response.data);
    } catch (error) {
      console.error('Error al obtener los productos:', error);
    }
  };

  const handleInputChange = (e) => {
    setNewProduct({ ...newProduct, [e.target.name]: e.target.value });
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    setNewProduct({ ...newProduct, image: file });
  };

  const addProduct = async () => {
    try {
      const formData = new FormData();
      formData.append('name', newProduct.name);
      formData.append('price', newProduct.price);
      formData.append('image', newProduct.image);

      const response = await axios.post('http://localhost:3001/products', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setProducts([...products, response.data]);
      setNewProduct({ name: '', price: '', image: null });
    } catch (error) {
      console.error('Error al agregar el producto:', error);
    }
  };

  const deleteProduct = async (id) => {
    try {
      await axios.delete(`http://localhost:3001/products/${id}`);
      setProducts(products.filter((product) => product.id !== id));
    } catch (error) {
      console.error('Error al eliminar el producto:', error);
    }
  };

  const editProduct = (product) => {
    setEditingProduct(product);
    setNewProduct({ name: product.name, price: product.price, image: null });
  };

  const updateProduct = async () => {
    try {
      const formData = new FormData();
      formData.append('name', newProduct.name);
      formData.append('price', newProduct.price);
      if (newProduct.image) {
        formData.append('image', newProduct.image);
      }

      const response = await axios.put(`http://localhost:3001/products/${editingProduct.id}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setProducts(products.map((product) => (product.id === response.data.id ? response.data : product)));
      setEditingProduct(null);
      setNewProduct({ name: '', price: '', image: null });
    } catch (error) {
      console.error('Error al actualizar el producto:', error);
    }
  };

  const handleLogout = () => {
    // Aquí puedes realizar la lógica para cerrar la sesión
    window.location.href = '/'; // Redirige a la página principal
  };

  return (
    <div className="panel-admin">
      <h2>Panel de Administración</h2>
      <div className="form-container">
        <h3>{editingProduct ? 'Editar Producto' : 'Agregar Producto'}</h3>
        <div className="form-group">
          <label htmlFor="name">Nombre:</label>
          <input
            type="text"
            id="name"
            name="name"
            value={newProduct.name}
            onChange={handleInputChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="price">Precio:</label>
          <input
            type="number"
            id="price"
            name="price"
            value={newProduct.price}
            onChange={handleInputChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="image">Imagen:</label>
          <input type="file" id="image" name="image" onChange={handleImageUpload} />
        </div>
        {editingProduct ? (
          <button className="btn btn-primary" onClick={updateProduct}>
            Guardar Cambios
          </button>
        ) : (
          <button className="btn btn-primary" onClick={addProduct}>
            Agregar Producto
          </button>
        )}
      </div>
      <div className="product-list">
        <h3>Lista de Productos</h3>
        <ul>
          {products.map((product) => (
            <li key={product.id}>
              <div className="product-info">
                <img src={`http://localhost:3001/${product.image}`} alt={product.name} />
                <div>
                  <h4>{product.name}</h4>
                  <p>Precio: ${product.price}</p>
                </div>
              </div>
              <div className="actions">
                <button className="btn btn-edit" onClick={() => editProduct(product)}>
                  <FaEdit />
                </button>
                <button className="btn btn-delete" onClick={() => deleteProduct(product.id)}>
                  <FaTrash />
                </button>
              </div>
            </li>
          ))}
        </ul>
      </div>
      <button className="btn btn-logout" onClick={handleLogout}>
        Guardar Cambios y Cerrar Sesión
      </button>
    </div>
  );
};

export default PanelAdmin;
