import React from 'react';
import { Link, useLocation} from 'react-router-dom';
import logobarb from './Logobarb.png'




function Navbar({ isLoggedIn, cart, handleLogout }) {
  const location = useLocation();
  const isAdminPage = location.pathname === '/admin';
  const [isHovered, setIsHovered] = useState(false);

  return (
    <nav className="navbar" style={{ backgroundColor: 'pink' }}>
      <Link to="/" className="navbar-brand">
        <img src={logobarb} alt="Logo" />
      </Link>
      {!isAdminPage && (
        <ul className="navbar-nav">
          <li className="nav-item">
            <Link to="/" className="nav-link">
              Inicio
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/cart" className="nav-link">
              Carrito ({cart.length})
            </Link>
          </li>
          {!isLoggedIn ? (
            <>
              <li className="nav-item">
                <Link to="/login" className="nav-link">
                  Iniciar sesión
                </Link>
              </li>
              <li className="nav-item">
                <Link to="/register" className="nav-link">
                  Registrarse
                </Link>
              </li>
            </>
          ) : (
            <li className="nav-item">
              <button
                onClick={handleLogout}
                className="nav-link"
                style={{
                  backgroundColor: '#FF69B4',
                  color: 'white',
                  border: 'none',
                  cursor: 'pointer',
                  padding: '8px 15px',
                  borderRadius: '5px',
                  transition: 'background-color 0.3s ease'
                }}
              >
                Cerrar sesión
              </button>
            </li>
          )}
        </ul>
      )}
      {isAdminPage && isLoggedIn && (
       <button
       onClick={handleLogout}
       className="nav-link"
       onMouseEnter={() => setIsHovered(true)}
       onMouseLeave={() => setIsHovered(false)}
       style={{
         backgroundColor: isHovered ? '#40E0D0' : '#FF69B4',
         color: 'black',
         border: 'none',
         cursor: 'pointer',
         padding: '8px 15px',
         borderRadius: '5px',
         transition: 'background-color 0.3s ease'
       }}
     >
          Cerrar sesión
        </button>
      )}
    </nav>
  );
}

export default Navbar;
