import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';
import '../components/succespage.css';

function SuccessPage() {
  const [orderSaved, setOrderSaved] = useState(false);
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    const saveOrder = async () => {
      const searchParams = new URLSearchParams(location.search);
      const paymentId = searchParams.get('payment_id');
      const status = searchParams.get('status');

      if (status === 'approved' && !orderSaved) {
        try {
          const pendingOrder = JSON.parse(localStorage.getItem('pendingOrder') || '{}');
          
          if (!pendingOrder.cart || !pendingOrder.customerInfo) {
            console.error('No se encontr贸 informaci贸n de la orden pendiente');
            return;
          }

          const orderData = {
            paymentId,
            customerInfo: pendingOrder.customerInfo,
            cart: pendingOrder.cart,
            total: pendingOrder.total,
            status: 'completed',
            createdAt: new Date().toISOString()
          };

          // Verificar si la orden ya existe
          const existingOrders = await axios.get('http://localhost:3001/orders');
          const orderExists = existingOrders.data.some(order => order.paymentId === paymentId);

          if (!orderExists) {
            await axios.post('http://localhost:3001/orders', orderData);
            console.log('Orden guardada exitosamente');
            setOrderSaved(true);
            localStorage.removeItem('pendingOrder');
          } else {
            console.log('La orden ya existe, no se guardar谩 de nuevo');
            setOrderSaved(true);
          }
        } catch (error) {
          console.error('Error al guardar la orden:', error);
        }
      }
    };

    if (!orderSaved) {
      saveOrder();
    }
  }, [location, orderSaved]);

  const handleContinueShopping = () => {
    navigate('/');
  };


  return (
    <div className="success-container">
      <div className="success-content">
        <h1 className="success-title">隆Pago Exitoso!</h1>
        <div className="success-icon"></div>
        <p className="success-message">
          Gracias por tu compra. Nos contactaremos a la brevedad.
          驴Tuviste un problema? cu茅ntanos: 3816591850
        </p>
        <button className="continue-shopping-btn" onClick={handleContinueShopping}>
          Seguir Comprando
        </button>
      </div>
    </div>
  );
}

export default SuccessPage;
