import React from 'react';

function ProductCard({ product, onAddToCart }) {
  return (
    <div>
      <h3>{product.name}</h3>
      <p>Precio: ${product.price}</p>
      <button onClick={onAddToCart}>Agregar al carrito</button>
      showAlert
    </div>
  );
}

export default ProductCard;

