import React from 'react';
import CartItem from './CartItem';
import { WebCheckout } from 'mercadopago-react-webcheckout';

const ACCESS_TOKEN = 'APP_USR-4156613020681559-061022-b8275073b98aa6d82d42054d0a81aef0-613424466';
const PUBLIC_KEY = 'APP_USR-b3086340-df07-4c18-9e21-b4e8f09b4b02';


function Cart({ cart, onRemove, onQuantityChange }) {
  const total = cart.reduce((acc, item) => acc + item.price * item.quantity, 0);

  const handleRemove = (item) => {
    onRemove(item);
  };

  const handleQuantityChange = (item, newQuantity) => {
    onQuantityChange(item, newQuantity);
  };

  const handleCheckout = () => {
    // Construir los items y la preferencia de pago
    const items = cart.map((item) => ({
      title: item.name,
      quantity: item.quantity,
      currency_id: 'ARS', // Código de moneda
      unit_price: item.price,
    }));

    const preference = {
      items,
      back_urls: {
        success: 'https://example.com/success', // URL de éxito
        failure: 'https://example.com/failure', // URL de fallo
        pending: 'https://example.com/pending', // URL de pendiente
      },
    };

    // Iniciar el checkout de MercadoPago
    const checkout = new WebCheckout({
      accessToken: ACCESS_TOKEN,
      publicKey: PUBLIC_KEY,
      preference,
    });

    checkout.open();
  };


  return (
    <div className="cart-container">
      <h2>Carrito de Compras</h2>
      <ul className="cart-items">
        {cart.map((item) => (
          <CartItem
            key={item.id}
            item={item}
            onRemove={handleRemove}
            onQuantityChange={handleQuantityChange}
          />
        ))}
      </ul>
      <div className="cart-summary">
        <h3>Resumen de Compra</h3>
        <p className="total">Total: ${total.toFixed(2)}</p>
        <button className="payment-button" onClick={handleCheckout}>
          Pagar </button>
      </div>
    </div>
  );
}

export default Cart;

