import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminCrud = () => {
  const [products, setProducts] = useState([]);
  const [productName, setProductName] = useState('');
  const [productPrice, setProductPrice] = useState('');
  const [productImage, setProductImage] = useState(null);

  useEffect(() => {
    // Obtener los productos de la base de datos JSON
    axios.get('http://localhost:3001/products')
      .then(response => {
        setProducts(response.data);
      })
      .catch(error => {
        console.error('Error al obtener los productos:', error);
      });
  }, []);

  const handleAddProduct = () => {
    // Crear un nuevo producto
    const newProduct = {
      id: products.length + 1,
      name: productName,
      price: productPrice,
      image: productImage ? productImage.name : '',
    };

    // Agregar el nuevo producto a la base de datos JSON
    axios.post('http://localhost:3001/products', newProduct)
      .then(response => {
        setProducts([...products, response.data]);
        setProductName('');
        setProductPrice('');
        setProductImage(null);
      })
      .catch(error => {
        console.error('Error al agregar el producto:', error);
      });
  };

  const handleDeleteProduct = id => {
    // Eliminar un producto de la base de datos JSON
    axios.delete(`http://localhost:3001/products/${id}`)
      .then(() => {
        setProducts(products.filter(product => product.id !== id));
      })
      .catch(error => {
        console.error('Error al eliminar el producto:', error);
      });
  };

  const handleUpdateProduct = (id, updatedProduct) => {
    // Actualizar un producto en la base de datos JSON
    axios.put(`http://localhost:3001/products/${id}`, updatedProduct)
      .then(response => {
        setProducts(products.map(product => (product.id === id ? response.data : product)));
      })
      .catch(error => {
        console.error('Error al actualizar el producto:', error);
      });
  };

  const handleImageChange = event => {
    setProductImage(event.target.files[0]);
  };

  const handleFinishChanges = () => {
    // Lógica para guardar los cambios y cerrar la sesión
    // ...
  };

  return (
    <div className="crud-container">
      <h2 className="nuevo">Agrega un nuevo producto</h2>
      <div className="form-group">
        <label>Nombre:</label>
        <input
          type="text"
          value={productName}
          onChange={e => setProductName(e.target.value)}
        />
      </div>
      <div className="form-group">
        <label>Precio:</label>
        <input
          type="text"
          value={productPrice}
          onChange={e => setProductPrice(e.target.value)}
        />
      </div>
      <div className="form-group">
        <label>Imagen:</label>
        <input type="file" accept="image/*" onChange={handleImageChange} />
      </div>
      <button className="añadir" onClick={handleAddProduct}>
        Añadir
      </button>
      <button className="btn btn-finish" onClick={handleFinishChanges}>
        Guardar cambios y cerrar sesión
      </button>
      <h2 className="lista">Lista de productos</h2>
      <ul className="product-list">
        {products.map(product => (
          <li key={product.id} className="product-item">
            <img src={`/images/${product.image}`} alt={product.name} />
            <span>{product.name}</span>
            <span>{product.price}</span>
            <button onClick={() => handleDeleteProduct(product.id)}>
              Eliminar
            </button>
            <button
              onClick={() =>
                handleUpdateProduct(product.id, {
                  ...product,
                  name: prompt('Nuevo nombre', product.name),
                  price: prompt('Nuevo precio', product.price),
                  image: productImage ? productImage.name : product.image,
                })
              }
            >
              Modificar
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminCrud;



