import React, { useState, useEffect } from 'react';
import axios from 'axios';

function AdminCrud() {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    name: '',
    price: '',
    image: ''
  });
  const [alertMessage, setAlertMessage] = useState('');

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get('http://localhost:3001/products');
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const handleInputChange = (e) => {
    setNewProduct({
      ...newProduct,
      [e.target.name]: e.target.value
    });
  };

  const handleAddProduct = async () => {
    try {
      const formData = new FormData();
      formData.append('name', newProduct.name);
      formData.append('price', newProduct.price);
      formData.append('image', newProduct.image);
  
      if (editing) {
        await axios.put(`http://localhost:3001/products/${newProduct.id}`, formData);
        setAlertMessage('Producto actualizado con éxito');
      } else {
        const response = await axios.post('http://localhost:3001/products', formData);
        setNewProduct({ id: response.data.id, name: '', price: '', image: null });
        setAlertMessage('Producto añadido con éxito');
      }
      setEditing(false);
      fetchProducts();
    } catch (error) {
      console.error('Error adding/updating product:', error);
    }
  };

  const handleDeleteProduct = async (id) => {
    try {
      await axios.delete(`http://localhost:3001/products/${id}`);
      setAlertMessage('Producto eliminado');
      fetchProducts();
    } catch (error) {
      console.error('Error deleting product:', error);
    }
  };

  const handleLogout = () => {
    // Lógica para cerrar sesión
    console.log('Sesión cerrada');
  };

  return (
    <div>
      <h1>Administración de Productos</h1>
      <div>
        <h2>Añadir Producto</h2>
        <input
          type="text"
          name="name"
          placeholder="Nombre"
          value={newProduct.name}
          onChange={handleInputChange}
        />
        <input
          type="number"
          name="price"
          placeholder="Precio"
          value={newProduct.price}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="image"
          placeholder="URL de la imagen"
          value={newProduct.image}
          onChange={handleInputChange}
        />
        <button onClick={handleAddProduct}>Añadir</button>
      </div>
      {alertMessage && <p>{alertMessage}</p>}
      <ul>
        {products.map((product) => (
          <li key={product.id}>
            <img src={product.image} alt={product.name} />
            <p>Nombre: {product.name}</p>
            <p>Precio: {product.price}</p>
            <button onClick={() => handleDeleteProduct(product.id)}>
              Eliminar
            </button>
          </li>
        ))}
      </ul>
      <button onClick={handleLogout}>Guardar cambios y cerrar sesión</button>
    </div>
  );
}

export default AdminCrud;
