import React, { useState } from 'react';
import ProductCard from './ProductCard';
import layout1 from '../layout1.jpg';
import layout2 from '../layout2.jpg';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
function ProductList({ products, onAddToCart }) {
  const [currentLayout, setCurrentLayout] = useState('layout1');

  const handleLayoutChange = (newLayout) => {
    setCurrentLayout(newLayout);
  };

  const handleAddToCart = (product) => {
    onAddToCart(product);
    toast.success('Producto agregado al carrito', {
      position: 'top-right',
      autoClose: 3000,
      hideProgressBar: false,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
      progress: undefined,
      style: { backgroundColor: 'pink', color: 'black' },
    });
  };
 return (
    <div>
      <ToastContainer />
      <div className="layout-container">
        <img
          src={layout1}
          alt="Layout 1"
          className={`layout-image ${currentLayout === 'layout1' ? 'active' : ''}`}
          onClick={() => handleLayoutChange('layout1')}
          onMouseEnter={() => handleLayoutChange('layout1')}
        />
        <img
          src={layout2}
          alt="Layout 2"
          className={`layout-image ${currentLayout === 'layout2' ? 'active' : ''}`}
          onClick={() => handleLayoutChange('layout2')}
          onMouseEnter={() => handleLayoutChange('layout2')}
        />
      </div>
      <div className="container-productos">
        {products.map((product) => (
          <ProductCard
            key={product.id}
            product={product}
            onAddToCart={() => handleAddToCart(product)}
          />
        ))}
      </div>
    </div>
  );
}
