import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { useNavigate } from 'react-router-dom';
import './admin.css';

function AdminCrud() {
  const [products, setProducts] = useState([]);
  const [productName, setProductName] = useState('');
  const [productPrice, setProductPrice] = useState('');
  const [productImage, setProductImage] = useState('');
  const [editingProduct, setEditingProduct] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get('http://localhost:3001/products');
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const handleInputChange = (e, field) => {
    switch (field) {
      case 'name':
        setProductName(e.target.value);
        break;
      case 'price':
        setProductPrice(e.target.value);
        break;
      case 'image':
        setProductImage(e.target.value);
        break;
      default:
        break;
    }
  };

  const handleAddProduct = async () => {
    try {
      const newProduct = {
        name: productName,
        description: productDescription,
        price: parseFloat(productPrice),
        image: productImage,
      };

      const response = await axios.post('http://localhost:3001/products', newProduct);
      setProducts([...products, response.data]);
      toast.success('Producto añadido con éxito', { toastId: 'add-product' });
      clearFields();
    } catch (error) {
      console.error('Error adding product:', error);
      toast.error('Error al añadir el producto', { toastId: 'add-product-error' });
    }
  };

  const handleDeleteProduct = async (id) => {
    try {
      await axios.delete(`http://localhost:3001/products/${id}`);
      toast.success('Producto eliminado', { toastId: 'delete-product' });
      fetchProducts();
    } catch (error) {
      console.error('Error deleting product:', error);
      toast.error('Error al eliminar el producto', { toastId: 'delete-product-error' });
    }
  };

  const handleEditProduct = (product) => {
    setEditingProduct(product);
    setProductName(product.name);
    setProductDescription(product.description);
    setProductPrice(product.price.toString());
    setProductImage(product.image);
  };

  const handleUpdateProduct = async () => {
    try {
      const updatedProduct = {
        name: productName,
        description: productDescription,
        price: parseFloat(productPrice),
        image: productImage,
      };

      await axios.put(`http://localhost:3001/products/${editingProduct.id}`, updatedProduct);
      toast.success('Producto actualizado con éxito', { toastId: 'update-product' });
      setEditingProduct(null);
      clearFields();
      fetchProducts();
    } catch (error) {
      console.error('Error updating product:', error);
      toast.error('Error al actualizar el producto', { toastId: 'update-product-error' });
    }
  };

  const clearFields = () => {
    setProductName('');
    setProductDescription('');
    setProductPrice('');
    setProductImage('');
  };

  const handleFinishChanges = () => {
    navigate(-1);
  };

  return (
    <div className="crud-container">
      <h2>{editingProduct ? 'Editar Producto' : 'Agregar Nuevo Producto'}</h2>
      <div className="form-group">
        <label>Nombre:</label>
        <input
          type="text"
          value={productName}
          onChange={(e) => handleInputChange(e, 'name')}
        />
      </div>
      <div className="form-group">
        <label>Descripción:</label>
        <input
          type="text"
          value={productDescription}
          onChange={(e) => handleInputChange(e, 'description')}
        />
      </div>
      <div className="form-group">
        <label>Precio:</label>
        <input
          type="text"
          value={productPrice}
          onChange={(e) => handleInputChange(e, 'price')}
        />
      </div>
      <div className="form-group">
        <label>Imagen:</label>
        <input
          type="text"
          value={productImage}
          onChange={(e) => handleInputChange(e, 'image')}
        />
      </div>
      {editingProduct ? (
        <button className="btn btn-update" onClick={handleUpdateProduct}>
          Actualizar
        </button>
      ) : (
        <button className="btn btn-add" onClick={handleAddProduct}>
          Añadir
        </button>
      )}
      <button className="btn btn-finish" onClick={handleFinishChanges}>
        Finalizar
      </button>

      <ToastContainer
        position="top-right"
        autoClose={3000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="colored"
        toastStyle={{ backgroundColor: '#ffc0cb', color: '#000' }}
      />

      <ul>
        {products.map((product) => (
          <li key={product.id}>
            <img src={product.image} alt={product.name} />
            <p>Nombre: {product.name}</p>
            <p>Descripción: {product.description}</p>
            <p>Precio: {product.price}</p>
            <button onClick={() => handleDeleteProduct(product.id)}>Eliminar</button>
            <button onClick={() => handleEditProduct(product)}>Modificar</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AdminCrud;
