import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from './components/Navbar';
import Login from './components/Login';
import Register from './components/Register';
import ProductList from './components/ProductList';
import AdminCrud from './components/PanelAdmin';
import Cart from './components/Cart/Cart';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import Testimonials from './components/Testimonials';
import { useNavigate } from 'react-router-dom';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [cart, setCart] = useState([]);
  const [showAlert, setShowAlert] = useState(false);
  const [products, setProducts] = useState([]);
  const location = useLocation();
  const isAdminPage = location.pathname === '/admin';
  useEffect(() => {
    const loggedIn = localStorage.getItem('isLoggedIn') === 'true';
    if (loggedIn) {
      setIsLoggedIn(true);
    }
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:3001/products');
        setProducts(response.data);
      } catch (error) {
        console.error('Error al obtener los productos:', error);
      }
    };
    fetchProducts();

    
 
  const checkAdminRoute = () => {
    const location = window.location;
    setIsAdminPage(location.pathname === '/admin');
  };

  // Llamar la función auxiliar después de que el componente se haya montado
  checkAdminRoute();

  // Agregar un event listener para detectar cambios en la ruta
  window.addEventListener('popstate', checkAdminRoute);

  // Limpiar el event listener al desmontar el componente
  return () => {
    window.removeEventListener('popstate', checkAdminRoute);
  };
}, []);
  

  const handleLogin = async (username, password) => {
    try {
      const response = await axios.post('http://localhost:3001/users', { username, password });
      
      if (response.data.length > 0) {
        setIsLoggedIn(true);
        localStorage.setItem('isLoggedIn', 'true');
        localStorage.setItem('user', JSON.stringify(response.data[0]));
        alert('Bienvenid@');
      } else {
        alert('Usuario o contraseña incorrectos');
      }
    } catch (error) {
      console.error('Error al iniciar sesión:', error);
      alert('Contraseña y/o Usuario incorrectos');
    }
  };
  
  const handleAddToCart = (product) => {
    setCart([...cart, { ...product, quantity: 1 }]);
    setShowAlert(true); // Actualizar el estado showAlert a true
    setTimeout(() => {
      setShowAlert(false); // Después de 3 segundos, establecer showAlert a false
    }, 3000);
  };

  const handleRemove = (item) => {
    const updatedCart = cart.filter((cartItem) => cartItem.id !== item.id);
    setCart(updatedCart);
  };

  const handleQuantityChange = (item, newQuantity) => {
    const updatedCart = cart.map((cartItem) =>
      cartItem.id === item.id ? { ...cartItem, quantity: newQuantity } : cartItem
    );
    setCart(updatedCart);
  };
  const handleLogout = () => {
    setIsLoggedIn(false);
    localStorage.removeItem('isLoggedIn');
    localStorage.removeItem('user');
    localStorage.removeItem('cart');  // Opcional: limpiar carrito al salir
    setCart([]);
  };
  const updateProducts = async (updatedProduct) => {
    try {
      await axios.put(`http://localhost:3001/products/${updatedProduct.id}`, updatedProduct);
      const response = await axios.get('http://localhost:3001/products');
      setProducts(response.data);
    } catch (error) {
      console.error('Error al actualizar el producto:', error);
    }
  };


  
  return (
    <Router>
      <div className="App">
        {!isAdminPage && <Navbar isLoggedIn={isLoggedIn} cart={cart} />}
  
        <Routes>
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          <Route path="/register" element={<Register />} />
          <Route
            path="/"
            element={
              <>
                <ProductList products={products} onAddToCart={handleAddToCart} />
                <Testimonials />
              </>
            }
          />
          <Route
            path="/cart"
            element={
              <Cart
                cart={cart}
                onRemove={handleRemove}
                onQuantityChange={handleQuantityChange}
                showAlert={showAlert}
              />
            }
          />
          <Route
             exact
             path="/admin"
             element={
               <AdminCrud products={products} updateProducts={updateProducts} />
             }
              />
        </Routes>
      </div>
    </Router>
  );
}

export default App;


