import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const onLogin = async (username, password) => {
  try {
    const response = await axios.get('http://localhost:3000/users', {
      params: {
        username,
        password,
      },
    });

    if (response.data.length > 0) {
      // Usuario autenticado correctamente
      console.log('Inicio de sesión exitoso');
      // Aquí puedes redirigir al usuario o hacer cualquier otra acción después del inicio de sesión exitoso
    } else {
      // Credenciales incorrectas
      console.log('Credenciales incorrectas');
    }
  } catch (error) {
    console.error('Error al iniciar sesión:', error);
  
};
  
  return (
    <div className="overlay">
      <form onSubmit={handleSubmit} className="login-form">
        <div className="con">
          <header className="head-form">
            <h2>Iniciar Sesión</h2>
            <p>Coloca tu usuario y contraseña</p>
          </header>
          <br />
          <div className="field-set">
            <span className="input-item">
              <i className="fa fa-user-circle"></i>
            </span>
            <input
              className="form-input"
              id="txt-input"
              type="text"
              placeholder="@usuario"
              value={username}
              onChange={handleUsernameChange}
              required
            />
            <br />
            <span className="input-item">
              <i className="fa fa-key"></i>
            </span>
            <input
              className="form-input"
              type={showPassword ? 'text' : 'password'}
              placeholder="Contraseña"
              id="pwd"
              name="password"
              value={password}
              onChange={handlePasswordChange}
              required
            />
            <span className="emojis" onClick={togglePasswordVisibility}>
            {showPassword ? '🙈 Ocultar' : '👀 Mostrar'}
          </span>
            <br />
            <button type="submit" className="log-in">
              Ingresar
            </button>
          </div>
          <div className="other">
            <span className="new-user">¿Nuevo? Regístrate aquí!</span>
            <Link to="/register" className="btn submits sign-up">
              Registrarse
              <i className="fa fa-user-plus" aria-hidden="true"></i>
            </Link>
          </div>
        </div>
      </form>
    </div>
  );
};

export default Login;
