import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./admin.css";

const AdminCrud = ({ products, updateProducts }) => {
  const [productName, setProductName] = useState("");
  const [productPrice, setProductPrice] = useState("");
  const [productImage, setProductImage] = useState("");
  const navigate = useNavigate();

  // Función para agregar un nuevo producto
  const handleAddProduct = () => {
    const newProduct = {
      name: productName,
      description: productDescription,
      price: parseFloat(productPrice),
      image: productImage,
    };

    fetch("http://localhost:3001/products", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newProduct),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Respuesta de la API:", data);
        // Si la API devuelve una respuesta exitosa, actualiza los productos en el estado
        updateProducts([...products, data]);
        // Limpiar los campos después de agregar el producto
        setProductName("");
        setProductDescription("");
        setProductPrice("");
        setProductImage("");
      })
      .catch((error) => console.error("Error al agregar producto:", error));
  };
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => {
      setProductImage(reader.result);
    };
  };
  // Función para finalizar los cambios y redirigir
  const handleFinishChanges = () => {
    navigate("/");
  };

  // Función para eliminar un producto
  const handleDeleteProduct = (productId) => {
    fetch(`http://localhost:3001/products/${productId}`, {
      method: "DELETE",
    })
      .then(() => {
        // Actualiza los productos en el estado eliminando el producto con el ID correspondiente
        updateProducts(products.filter((product) => product.id !== productId));
      })
      .catch((error) => console.error("Error al eliminar el producto:", error));
  };

  // Función para modificar un producto
  const handleUpdateProduct = (productId, updatedProduct) => {
    fetch(`http://localhost:3001/products/${productId}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(updatedProduct),
    })
      .then((response) => response.json())
      .then((data) => {
        // Actualiza los productos en el estado reemplazando el producto modificado
        updateProducts(
          products.map((product) => (product.id === productId ? data : product))
        );
      })
      .catch((error) => console.error("Error al modificar el producto:", error));
  };

  return (
    <div className="crud-container">
      <h2>Agrega un nuevo producto</h2>
      <div className="form-group">
        <label>Nombre:</label>
        <input
          type="text"
          value={productName}
          onChange={(e) => setProductName(e.target.value)}
        />
      </div>
      <div className="form-group">
        <label>Descripción:</label>
        <input
          type="text"
          value={productDescription}
          onChange={(e) => setProductDescription(e.target.value)}
        />
      </div>
      <div className="form-group">
        <label>Precio:</label>
        <input
          type="text"
          value={productPrice}
          onChange={(e) => setProductPrice(e.target.value)}
        />
      </div>
      <div className="form-group">
        <label>Imagen:</label>
        <input
          type="text"
          value={productImage}
          onChange={(e) => setProductImage(e.target.value)}
        />
      </div>
      <button className="btn btn-add" onClick={handleAddProduct}>
        Añadir
      </button>
      <button className="btn btn-finish" onClick={handleFinishChanges}>
        Guardar cambios y cerrar sesión
      </button>
      <h2>Lista de productos</h2>
      <ul className="product-list">
        {products.map((product) => (
          <li key={product.id}>
            <span>{product.name}</span>
            <span>{product.price}</span>
            <img src={product.image} alt={product.name} />
            <button onClick={() => handleDeleteProduct(product.id)}>
              Eliminar
            </button>
            <button
              onClick={() =>
                handleUpdateProduct(product.id, {
                  ...product,
                  name: prompt("Nuevo nombre", product.name),
                  price: prompt("Nuevo precio", product.price),
                  image: prompt("Nueva imagen", product.image),
                })
              }
            >
              Modificar
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminCrud;
