import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from './components/Navbar';
import Login from './components/Login';
import Register from './components/Register';
import ProductList from './components/ProductList';

import Cart from './components/Cart/Cart';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Testimonials from './components/Testimonials';
import { useNavigate } from 'react-router-dom';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [cart, setCart] = useState([]);
  const [showAlert, setShowAlert] = useState(false);
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const loggedIn = localStorage.getItem('isLoggedIn') === 'true';
    if (loggedIn) {
      setIsLoggedIn(true);
    }
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:3001/products');
        setProducts(response.data);
      } catch (error) {
        console.error('Error al obtener los productos:', error);
      }
    };
    fetchProducts();
    
  }, []);
  

  const handleLogin = async (username, password) => {
    try {
      const response = await axios.post('http://localhost:3001/users', { username, password });
      
      if (response.data.length > 0) {
        setIsLoggedIn(true);
        localStorage.setItem('isLoggedIn', 'true');
        localStorage.setItem('user', JSON.stringify(response.data[0]));
        alert('Bienvenid@');
      } else {
        alert('Usuario o contraseña incorrectos');
      }
    } catch (error) {
      console.error('Error al iniciar sesión:', error);
      alert('Contraseña y/o Usuario incorrectos');
    }
  };
  
  const handleAddToCart = (product) => {
    setCart([...cart, { ...product, quantity: 1 }]);
    setShowAlert(true); // Actualizar el estado showAlert a true
    setTimeout(() => {
      setShowAlert(false); // Después de 3 segundos, establecer showAlert a false
    }, 3000);
  };

  const handleRemove = (item) => {
    const updatedCart = cart.filter((cartItem) => cartItem.id !== item.id);
    setCart(updatedCart);
  };

  const handleQuantityChange = (item, newQuantity) => {
    const updatedCart = cart.map((cartItem) =>
      cartItem.id === item.id ? { ...cartItem, quantity: newQuantity } : cartItem
    );
    setCart(updatedCart);
  };
  const handleLogout = () => {
    setIsLoggedIn(false);
    localStorage.removeItem('isLoggedIn');
    localStorage.removeItem('user');
    localStorage.removeItem('cart');  // Opcional: limpiar carrito al salir
    setCart([]);
  };
  const express = require('express');
const axios = require('axios');
 const app = express();

app.use(express.urlencoded({ extended: true }));
app.use(express.json());
app.get('/products', async (req, res) => {
  try {
    const response = await axios.get('http://localhost:3001/products');
    res.json(response.data);
  } catch (error) {
    console.error(error);
    res.status(500).send('Error al obtener los productos');
  }
});

// Ruta para agregar un nuevo producto
app.post('/products', async (req, res) => {
  try {
    const { name, price, image } = req.body;
    const response = await axios.post('http://localhost:3001/products', {
      name,
      price,
      image,
    });
    res.json(response.data);
  } catch (error) {
    console.error(error);
    res.status(500).send('Error al agregar el producto');
  }
});

// Ruta para modificar un producto existente
app.put('/products/:id', async (req, res) => {
  try {
    const { id } = req.params;
    const { name, price, image } = req.body;
    const response = await axios.put(`http://localhost:3001/products/${id}`, {
      name,
      price,
      image,
    });
    res.json(response.data);
  } catch (error) {
    console.error(error);
    res.status(500).send('Error al modificar el producto');
  }
});

// Ruta para eliminar un producto
app.delete('/products/:id', async (req, res) => {
  try {
    const { id } = req.params;
    await axios.delete(`http://localhost:3001/products/${id}`);
    res.send('Producto eliminado exitosamente');
  } catch (error) {
    console.error(error);
    res.status(500).send('Error al eliminar el producto');
  }
});


  return (
    <Router>
      <div className="App">
        <Navbar isLoggedIn={isLoggedIn} cart={cart} />
        <Routes>
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          <Route path="/register" element={<Register />} />
          <Route
            path="/"
            element={
              <>
                <ProductList products={products} onAddToCart={handleAddToCart} />
                <Testimonials />
              </>
            }
          />
          <Route
            path="/cart"
            element={
              <Cart
                cart={cart}
                onRemove={handleRemove}
                onQuantityChange={handleQuantityChange}
                showAlert={showAlert}
              />
            }
          />
          <Route
            path="/admin"
            element={
              isLoggedIn ? (
                <>
                  <ProductList products={products} />
                  {<AddProduct onAdd={(newProduct) => setProducts([...products, newProduct])} /> }
                </>
              ) : (
                <Login onLogin={handleLogin} />
              )
            }
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;


