/*import React from 'react';
import CartItem from './CartItem';

function Cart({ cart }) {
  const total = cart.reduce((acc, item) => acc + item.price, 0);

  return (
    <div className="container">
      <h2>Carrito</h2>
      <ul className="list-group">
        {cart.map((item) => (
          <CartItem key={item.id} item={item} />
        ))}
      </ul>
      <div className="total">
        <p>Total: ${total}</p>
      </div>
    </div>
  );
}

export default Cart; */
