import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const DEFAULT_IMAGE = '/images/products/default.jpg'; // Ruta de la imagen por defecto

function AdminCrud() {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    name: '',
    price: '',
    image: DEFAULT_IMAGE, // Inicializamos la imagen con la imagen por defecto
    imageFile: null, // Nuevo estado para almacenar el archivo de imagen seleccionado
  });
  const [editingProduct, setEditingProduct] = useState(null);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get('http://localhost:3001/products');
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const handleInputChange = (e) => {
    if (e.target.name === 'imageFile') {
      setNewProduct({
        ...newProduct,
        imageFile: e.target.files[0],
      });
    } else {
      setNewProduct({
        ...newProduct,
        [e.target.name]: e.target.value,
      });
    }
  };

  const handleAddProduct = async () => {
    try {
      const formData = new FormData();
      formData.append('name', newProduct.name);
      formData.append('price', newProduct.price);
      if (newProduct.imageFile) {
        formData.append('image', newProduct.imageFile);
      }

      await axios.post('http://localhost:3001/products', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      toast.success('Producto añadido con éxito', { toastId: 'add-product' });
      setNewProduct({ name: '', price: '', image: DEFAULT_IMAGE, imageFile: null });
      fetchProducts();
    } catch (error) {
      console.error('Error adding product:', error);
      toast.error('Error al añadir el producto', { toastId: 'add-product-error' });
    }
  };

  const handleDeleteProduct = async (id) => {
    try {
      await axios.delete(`http://localhost:3001/products/${id}`);
      toast.success('Producto eliminado', { toastId: 'delete-product' });
      fetchProducts();
    } catch (error) {
      console.error('Error deleting product:', error);
      toast.error('Error al eliminar el producto', { toastId: 'delete-product-error' });
    }
  };

  const handleEditProduct = (product) => {
    setEditingProduct(product);
    setNewProduct(product);
  };

  const handleUpdateProduct = async () => {
    try {
      await axios.put(`http://localhost:3001/products/${editingProduct.id}`, newProduct);
      toast.success('Producto actualizado con éxito', { toastId: 'update-product' });
      setEditingProduct(null);
      setNewProduct({ name: '', price: '', image: '' });
      fetchProducts();
    } catch (error) {
      console.error('Error updating product:', error);
      toast.error('Error al actualizar el producto', { toastId: 'update-product-error' });
    }
  };

  const handleLogout = () => {
    // Lógica para cerrar sesión
    console.log('Sesión cerrada');
    // Redirigir a la página principal
    window.location.href = '/';
  };

  return (
    <div>
      <h1>Administración de Productos</h1>
      <div>
        <h2>Añadir/Modificar Producto</h2>
        <input
          type="text"
          name="name"
          placeholder="Nombre"
          value={newProduct.name}
          onChange={handleInputChange}
        />
        <input
          type="number"
          name="price"
          placeholder="Precio"
          value={newProduct.price}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="image"
          placeholder="URL de la imagen"
          value={newProduct.image}
          onChange={handleInputChange}
        />
        {editingProduct ? (
          <button onClick={handleUpdateProduct}>Modificar</button>
        ) : (
          <button onClick={handleAddProduct}>Añadir</button>
        )}
      </div>
      <ToastContainer
        position="top-right"
        autoClose={3000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="colored"
        toastStyle={{ backgroundColor: '#ffc0cb', color: '#000' }} // Tono rosa
      />
      <ul>
        {products.map((product) => (
          <li key={product.id}>
            <img src={product.image} alt={product.name} />
            <p>Nombre: {product.name}</p>
            <p>Precio: {product.price}</p>
            <button onClick={() => handleDeleteProduct(product.id)}>Eliminar</button>
            <button onClick={() => handleEditProduct(product)}>Modificar</button>
          </li>
        ))}
      </ul>
      <button onClick={handleLogout}>Guardar cambios y cerrar sesión</button>
    </div>
  );
}

export default AdminCrud;
