import React, { useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';

const Login = ({ onLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();
  useEffect(() => {
    const isLoggedIn = localStorage.getItem('isLoggedIn') === 'true';
    if (isLoggedIn) {
      console.log('Usuario ya autenticado, redirigiendo...');
      navigate('/');
    }
  }, [navigate]);

  const handleUsernameChange = (e) => {
    setUsername(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const result = await onLogin(username, password);
    alert(result.message);
    if (result.success) {
      navigate(result.redirectTo);
    }
  };

  return (
    <div className="overlay">
      <form onSubmit={handleSubmit} className="login-form">
        <div className="con">
          <header className="head-form">
            <h3>Iniciar SesiÃ³n</h3>
            <p>Coloca tu usuario y contraseÃ±a</p>
          </header>
          <br />
          <div className="field-set">
            <span className="input-item">
              <i className="fa fa-user-circle"></i>
              

            </span>
            <input
              className="form-input"
              id="username" // Agrega un id
              type="text"
              placeholder="@usuario"
              value={username}
              onChange={handleUsernameChange}
              required
            />
            <br />
            <span className="input-item">
              <i className="fa fa-key"></i>
            </span>
            <input
              className="form-input"
              id="password" // Agrega un id
              type={showPassword ? 'text' : 'password'}
              placeholder="ContraseÃ±a"
              value={password}
              onChange={handlePasswordChange}
              required
            />
            <span className="emojis" onClick={togglePasswordVisibility}>
              {showPassword ? 'ðŸ™ˆ Ocultar' : 'ðŸ‘€ Mostrar'}
            </span>
            <br />
            <button type="submit" className="log-in">
              Ingresar
            </button>
          </div>
          <div className="other">
            <span className="new-user">Â¿Nuevo? RegÃ­strate aquÃ­!</span>
            <Link to="/register" className="btn submits sign-up">
              Registrarse
              <i className="fa fa-user-plus" aria-hidden="true"></i>
            </Link>
          </div>
        </div>
      </form>
    </div>
  );
};

export default Login;
