// SuccessPage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';

function SuccessPage() {
  const location = useLocation();
  const navigate = useNavigate();
  const [orderDetails, setOrderDetails] = useState(null);

  useEffect(() => {
    const searchParams = new URLSearchParams(location.search);
    const paymentId = searchParams.get('payment_id');
    const status = searchParams.get('status');

    if (status === 'approved') {
      const cart = JSON.parse(localStorage.getItem('cart') || '[]');
      const customerInfo = JSON.parse(localStorage.getItem('customerInfo') || '{}');
      const total = cart.reduce((acc, item) => acc + item.price * item.quantity, 0);

      const orderData = {
        paymentId,
        customerInfo,
        cart,
        total,
        additionalNotes: customerInfo.additionalNotes || '',
      };

      axios.post('http://localhost:3001/orders', orderData)
        .then(response => {
          console.log('Compra realizada exitosamente');
          setOrderDetails(orderData);
          localStorage.removeItem('cart');
          localStorage.removeItem('customerInfo');
        })
        .catch(error => {
          console.error('Error al enviar la información del pedido:', error);
        });
    }
  }, [location]);

  const handleContinueShopping = () => {
    navigate('/');
  };

  return (
    <div>
      <h1>¡Pago Exitoso!</h1>
      <p>Gracias por tu compra. Nos contactaremos a la brevedad</p>
      {orderDetails && (
        <div>
          <h2>Detalles de la compra:</h2>
          <p>Nombre del cliente: {orderDetails.customerInfo.name}</p>
          <p>Cantidad total de muñecas: {orderDetails.cart.reduce((acc, item) => acc + item.quantity, 0)}</p>
          <h3>Muñecas compradas:</h3>
          <ul>
            {orderDetails.cart.map((item, index) => (
              <li key={index}>{item.name} - Cantidad: {item.quantity}</li>
            ))}
          </ul>
          <p>Total: ${orderDetails.total.toFixed(2)}</p>
        </div>
      )}
      <button onClick={handleContinueShopping}>Seguir Comprando</button>
    </div>
  );
}

export default SuccessPage;
