import React from 'react';
import ProductCard from './ProductCard';
import layout1 from './layout1.png';
import layout2 from './layout2.jpg';

function ProductList({ products, onAddToCart }) {
  const [currentLayout, setCurrentLayout] = useState('layout1');

  const handleLayoutClick = (layout) => {
    setCurrentLayout(layout);
    // No es necesario un return aquí
  };

  return (
    <div>
      <div className="layout-container">
        <img
          src={layout1}
          alt="Layout 1"
          className={`layout-image ${currentLayout === 'layout1' ? 'active' : ''}`}
          onClick={() => handleLayoutClick('layout1')}
        />
        <img
          src={layout2}
          alt="Layout 2"
          className={`layout-image ${currentLayout === 'layout2' ? 'active' : ''}`}
          onClick={() => handleLayoutClick('layout2')}
        />
      </div>
      <div className="container-productos">
        {products.map((product) => (
          <ProductCard
            key={product.id}
            product={product}
            onAddToCart={onAddToCart}
          />
        ))}
      </div>
    </div>
  );
}

export default ProductList;
