import React, { useState, useEffect, useRef } from 'react';
import CartItem from './CartItem';
import axios from 'axios';

function Cart({ cart, onRemove, onQuantityChange }) {
  const [showPaymentForm, setShowPaymentForm] = useState(false);
  const [customerInfo, setCustomerInfo] = useState({
    name: '',
    phone: '',
    dni: '',
    email: '',
    additionalNotes: ''
  });
  const [bricksBuilder, setBricksBuilder] = useState(null);
  const [preferenceId, setPreferenceId] = useState(null);
  const brickRef = useRef(null);

  useEffect(() => {
    const loadMercadoPago = async () => {
      const script = document.createElement('script');
      script.src = "https://sdk.mercadopago.com/js/v2";
      script.async = true;
      script.onload = () => {
        const mp = new window.MercadoPago('TEST-39b72eae-048a-4888-9401-6910724b5b7c', {
          locale: 'es-AR'
        });
        setBricksBuilder(mp.bricks());
      };
      document.body.appendChild(script);
    };

    loadMercadoPago();
  }, []);

  useEffect(() => {
    if (bricksBuilder && preferenceId) {
      const renderCheckoutBrick = async () => {
        const settings = {
          initialization: {
            preferenceId: preferenceId,
          },
          callbacks: {
            onReady: () => {
              console.log('Brick listo');
            },
            onSubmit: () => {
              console.log('Pago enviado');
            },
            onError: (error) => {
              console.error('Error en el pago:', error);
            },
          },
        };

        try {
          const brick = await bricksBuilder.create('payment', 'mercadopago-container', settings);
          brickRef.current = brick;
        } catch (error) {
          console.error('Error al crear el Brick:', error);
        }
      };

      renderCheckoutBrick();
    }
  }, [bricksBuilder, preferenceId]);

  const total = cart.reduce((acc, item) => acc + item.price * item.quantity, 0);

  const handleInputChange = (e) => {
    setCustomerInfo({ ...customerInfo, [e.target.name]: e.target.value });
  };

  const handleShowPaymentForm = () => {
    setShowPaymentForm(true);
  };

  const handlePayment = async (e) => {
    e.preventDefault();
    
    if (!bricksBuilder) {
      console.error('MercadoPago no está inicializado');
      return;
    }

    try {
      const response = await axios.post('http://localhost:8000/create_preference', {
        items: cart.map(item => ({
          title: item.name,
          unit_price: item.price,
          quantity: item.quantity,
        })),
        payer: {
          name: customerInfo.name,
          email: customerInfo.email,
          identification: {
            type: 'DNI',
            number: customerInfo.dni
          }
        }, 
        additionalNotes: customerInfo.additionalNotes
      });

      const { id } = response.data;

      if (id) {
        const orderData = {
          paymentId: id,
          customerInfo,
          cart,
          total,
          status: 'pending',
          createdAt: new Date().toISOString()
        };

        await axios.post('http://localhost:3001/orders', orderData);

        setPreferenceId(id);
      } else {
        console.error('No se pudo crear la preferencia de pago');
      }
    } catch (error) {
      console.error('Error al crear la preferencia de pago:', error);
    }
  };

  return (
    <div className="cart-container">
      <h2 className="cart-title">Mi carrito</h2>
      <ul className="cart-items">
        {cart.map((item) => (
          <CartItem
            key={item.id}
            item={item}
            onRemove={onRemove}
            onQuantityChange={onQuantityChange}
          />
        ))}
      </ul>
      <div className="cart-summary">
        <h3 className="summary-title">Resumen de la Compra</h3>
        <p className="total">Total a pagar: ${total.toFixed(2)}</p>
        {!showPaymentForm ? (
          <button className="payment-button" onClick={handleShowPaymentForm}>Proceder al pago</button>
        ) : (
          <form onSubmit={handlePayment} className="payment-form">
            <input
              type="text"
              name="name"
              value={customerInfo.name}
              onChange={handleInputChange}
              placeholder="Nombre completo"
              required
            />
            <input
              type="tel"
              name="phone"
              value={customerInfo.phone}
              onChange={handleInputChange}
              placeholder="Número de teléfono"
              required
            />
            <input
              type="text"
              name="dni"
              value={customerInfo.dni}
              onChange={handleInputChange}
              placeholder="DNI"
              required
            />
            <input
              type="email"
              name="email"
              value={customerInfo.email}
              onChange={handleInputChange}
              placeholder="Correo electrónico"
              required
            />
            <textarea
              name="additionalNotes"
              value={customerInfo.additionalNotes}
              onChange={handleInputChange}
              placeholder="Notas adicionales"
            />
            <p className="pickup-info">Punto de retiro: Colon 1358 de Lunes a Viernes de 8 am a 15 pm</p>
            
            <button type="submit" className="mercadopago-button">Pagar con MercadoPago</button>
            <div id="mercadopago-container"></div>
          </form>
        )}
      </div>
    </div>
  );
}

export default Cart;
