import React from 'react';

function CartItem({ item, onRemove, onQuantityChange }) {
  const { name, price, image, quantity } = item;

  const handleQuantityChange = (newQuantity) => {
    onQuantityChange(item, newQuantity);
  };

  const handleQuantityDecrement = () => {
    handleQuantityChange(Math.max(quantity - 1, 1));
  };

  const handleQuantityIncrement = () => {
    handleQuantityChange(quantity + 1);
  };

  const handleInputChange = (e) => {
    const newQuantity = parseInt(e.target.value, 10) || 1;
    handleQuantityChange(newQuantity);
  };

  return (
    <div className="cart-item">
      <div className="cart-item-image-container">
      {}
        {product.image.startsWith('http') ? (
          // Si la imagen proviene de Cloudinary
          <img src={image} alt={product.name} />
        ) : (
          // Si la imagen proviene del JSON
          <img src={`/images/${image}`} alt={product.name} />
        )}
      </div>
      <div className="cart-item-details">
        <h5>{name}</h5>
        <p>Precio: ${price}</p>
        <div className="cart-item-actions">
          <div className="quantity-control">
            <button onClick={handleQuantityDecrement}>-</button>
            <input type="number" value={quantity} min="1" onChange={handleInputChange} />
            <button onClick={handleQuantityIncrement}>+</button>
          </div>
          <p className="subtotal">Subtotal: ${(price * quantity).toFixed(2)}</p>
          <button className="remove-button" onClick={() => onRemove(item)}>
            Eliminar
          </button>
        </div>
      </div>
    </div>
  );
}

export default CartItem;
